package com.dam.aplicacionandroidpersonalizada;

import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.RadioButton;
import android.widget.Toast;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import java.io.BufferedWriter;
import java.io.File;
import java.io.FileWriter;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.HashMap;
import java.util.Map;

public class Cafeteria extends AppCompatActivity {

    //Creamos los objetos
    RadioButton expressoRB;
    RadioButton americanoRB;
    RadioButton conLecheRB;
    RadioButton bombonRB;

    Button añadirCafeB;
    Button volverCafeB;
    Button irConsultaCafeB;

    Map<String, Double> preciosCafe;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_cafeteria);
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });
        //Relacionamos parte grafica y logica

        //RadioButtons.
        expressoRB = findViewById(R.id.cafeExpresoRB);
        americanoRB = findViewById(R.id.cafeAmericanoRB);
        conLecheRB = findViewById(R.id.cafeLecheRB);
        bombonRB = findViewById(R.id.cafeBombonRB);

        //Buttons.
        añadirCafeB = findViewById(R.id.añadirCafeButton);
        volverCafeB = findViewById(R.id.volverCafeButton);
        irConsultaCafeB = findViewById(R.id.irReciboCafeButton);

        // Inicializa el mapa de precios de café.
        preciosCafe = new HashMap<>();
        preciosCafe.put("Café Expreso", 1.50);
        preciosCafe.put("Café Americano", 2.00);
        preciosCafe.put("Café con Leche", 2.50);
        preciosCafe.put("Café Bombón", 3.00);
    }

    public void añadirCafe(View view){

        File file = new File(getFilesDir(), "Recibo.txt");
        try (FileWriter fileWriter = new FileWriter(file, true);
             BufferedWriter bufferedWriter = new BufferedWriter(fileWriter)){

            String tipoCafe = null;
            double precio = 0;

            // Comprobar cuál RadioButton está seleccionado.
            if (expressoRB.isChecked()) {
                tipoCafe = "Café Expresso";
                precio = preciosCafe.get(tipoCafe);
            } else if (americanoRB.isChecked()) {
                tipoCafe = "Café Americano";
                precio = preciosCafe.get(tipoCafe);
            } else if (conLecheRB.isChecked()) {
                tipoCafe = "Café con Leche";
                precio = preciosCafe.get(tipoCafe);
            } else if (bombonRB.isChecked()) {
                tipoCafe = "Café Bombón";
                precio = preciosCafe.get(tipoCafe);
            } else {
                bufferedWriter.write("Ningún café seleccionado.\n");
                Intent go = new Intent(this, ConsultarProductos.class);
                startActivity(go);
                return;
            }

            //Añadimos el cafe en el fichero.
            bufferedWriter.write(tipoCafe + " añadido. Precio: $" + precio + "\n");
            Toast.makeText(this,"Producto añadido con éxito",Toast.LENGTH_SHORT).show();

        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }

    public void goBackCatalog(View view){
        finish();
    }

    public void gotoConsulta(View view){
        Intent go = new Intent(this, ConsultarProductos.class);
        startActivity(go);
    }
}

package com.dam.aplicacionandroidpersonalizada;

import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.ImageButton;
import android.widget.TextView;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

public class Catalogo extends AppCompatActivity {
    //Declaración de variables de instancia.
    TextView welcomeTV;
    ImageButton cafeIM;
    ImageButton pastelIM;
    ImageButton saladosIM;
    Button goBack;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_catalogo);
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        //Relacionamos cada elemento xml con el código.
        welcomeTV = findViewById(R.id.welcomeTV);

        //Botones
        cafeIM = findViewById(R.id.cafeImButton);
        pastelIM = findViewById(R.id.pastelImButton);
        saladosIM = findViewById(R.id.saladoImButton);
        goBack = findViewById(R.id.backButton);

        //Recibimos el String enviado desde la anterior Activity.
        String nombre =  getIntent().getStringExtra("nombre");

        //Nos aseguramos de tratar con posible dato nulo y lo mostramos.
        String nombreUsuario = (nombre != null? nombre : "invitado");
        String mensaje = getString(R.string.welcomeMessage, nombreUsuario);

        welcomeTV.setText(mensaje);
    }

    public void goToCafeteria(View view){
        Intent next = new Intent(this, Cafeteria.class);
        startActivity(next);
    }

    public void goToPasteleria(View view){
        Intent next = new Intent(this, Pasteleria.class);
        startActivity(next);
    }

    public void goToSalados(View view){
        Intent next = new Intent(this, Salados.class);
        startActivity(next);
    }

    public void goBackMain(View view){
        Intent back = new Intent(this, MainActivity.class);
        startActivity(back);
        finish();
    }
}

package com.dam.aplicacionandroidpersonalizada;

import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.EditText;
import android.widget.TextView;
import android.widget.Toast;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import java.io.BufferedReader;
import java.io.File;
import java.io.FileNotFoundException;
import java.io.FileReader;
import java.io.IOException;

public class ConsultarProductos extends AppCompatActivity {

    TextView consultaTitleTV;
    EditText consultaListET;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_consultar_productos);
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        consultaListET=findViewById(R.id.consultaListET);
        consultaTitleTV=findViewById(R.id.consultaTitleTV);


        String mensaje = getString(R.string.consultaTitle);

        consultaTitleTV.setText(mensaje);
        File file = new File(getFilesDir(), "Recibo.txt");

        try (FileReader fileReader = new FileReader(file);
             BufferedReader bufferedReader = new BufferedReader(fileReader)){

            String linea;
            StringBuilder stringBuilder = new StringBuilder();

            // Lee línea por línea y agrega al StringBuilder
            while ((linea = bufferedReader.readLine()) != null) {
                stringBuilder.append(linea).append("\n"); // Añadir línea al contenido
            }

            // Mostrar el contenido en el TextView
            consultaListET.setText(stringBuilder.toString());

        } catch (FileNotFoundException e) {
            consultaListET.setText("Canasta vacía.");
        } catch (IOException e) {
            consultaListET.setText("Error al leer el archivo.");
        }
    }

    public void goBackConsulta(View view){
        Intent back = new Intent(this, Catalogo.class);
        startActivity(back);
    }

    public void deleteList(View view) {
        File file = new File(getFilesDir(), "Recibo.txt");

        if (file.exists()){
            file.delete();
            Toast.makeText(this,"Lista eliminada.", Toast.LENGTH_SHORT).show();
        } else {
            Toast.makeText(this,"No hay lista que eliminar.",Toast.LENGTH_SHORT).show();
        }
    }
}

package com.dam.aplicacionandroidpersonalizada;

import android.content.Intent;
import android.os.Bundle;
